const { count, direction, speed } = msg.payload

// if machine stop or error, don't move
if (
  global.get("status") === "stop"
  || global.get("status") === "error"
) {
  return [null, msg]
}

// if limit switches set, don't move further towards them
if (
  (direction === "left" && global.get("limitXMax"))
  || (direction === "right" && global.get("limitXMin"))
) {
  return [null, msg]
}

// if not homed, only allowed to go at slow speed
if (!global.get("hasHome") && speed === "slow") {
  return [msg, null]
}

// if pulses will move beyond min / max, don't move
const countVelocity =
  direction === "left"
    ? count
    : direction === "right"
      ? -count
      : 0
const prevPositionX = global.get("positionXInPulses")
const nextPositionX = prevPositionX + countVelocity
if (
  (nextPositionX < 0)
  || (nextPositionX > global.get("maxDistanceXInPulses"))
) {
  return [null, msg]
}

return [msg, null]

if (msg.topic !== "moveX") return msg

const { distance, direction, speed } = msg.payload

return {
  topic: "pulseX",
  payload: {
    count: distance * global.get("pulsesPerMeter"),
    direction,
    speed
  }
}

if (global.get("cycleStep") < global.get("numberOfStepsPerCycle") - 1) {
  return [msg, null]
} else {
  return [null, msg]
}

if (msg.topic !== "moveSideOneGridUnit") return msg

if (msg.payload !== "left" && msg.payload !== "right") {
  throw new Error("expected msg.payload as either left or right")
}

return {
  topic: "moveX",
  payload: {
    distance: 0.04,
    direction: msg.payload,
    speed: "fast"
  }
}

const { direction } = msg.payload

// if machine stop or error, don't move
if (
  global.get("status") === "stop"
  || global.get("status") === "error"
) {
  return [null, msg]
}

// if limit switches set, don't move further towards them
if (
  (direction === "up" && global.get("limitYMax"))
  || (direction === "down" && global.get("limitYMin"))
) {
  return [null, msg]
}

return [msg, null]

const motorSpeed = global.get("motorSpeed")
const targetMotorSpeed = flow.get("targetMotorSpeed")

if (motorSpeed === targetMotorSpeed) {
  return [msg, null, null]
} else if (motorSpeed < targetMotorSpeed) {
  return [null, msg, null]
} else {
  return [null, null, msg]
}

const positionXInPulses = global.get("positionXInPulses")
const count = Math.abs(positionXInPulses)
const direction = positionXInPulses > 0 ? "right" : "left"
const speed = "fast"

return {
  topic: "pulseX",
  payload: {
    count,
    direction,
    speed
  }
}
